<?php

namespace Composer;

use Composer\Semver\VersionParser;






class InstalledVersions
{
private static $installed = array (
  'root' => 
  array (
    'pretty_version' => 'dev-main',
    'version' => 'dev-main',
    'aliases' => 
    array (
    ),
    'reference' => 'd7a951da6179fe28c1f9b9b35b17176c90d1065e',
    'name' => 'parceladousa/gateway',
  ),
  'versions' => 
  array (
    'klebervmv/easycurl' => 
    array (
      'pretty_version' => '1.0.5',
      'version' => '1.0.5.0',
      'aliases' => 
      array (
      ),
      'reference' => '118d58069a1ea81e75c1ce1b228b5a2a1b104015',
    ),
    'parceladousa/gateway' => 
    array (
      'pretty_version' => 'dev-main',
